create virtual view if not exists view__library_tracks_with_album as 
      select
        _tracks.name as name,
        _tracks.id as id,
        _tracks.durationMs as durationMs,
        _tracks.trackNumber as trackNumber,
        _tracks.imageUrl as imageUrl,
        _tracks.albumId as albumId,
        _tracks.description as description,
        _tracks.externalLink as externalLink,
        _albums.name as albumName,
        _albums.trackCount as albumTrackCount,
        _albums.colorsString as albumColorsString
      from library_tracks as _tracks
        left outer join library_albums as _albums on _tracks.albumId = _albums.id
    ;
create  index if not exists view__library_tracks_with_album_by_id on view__library_tracks_with_album (id);
create  index if not exists view__library_tracks_with_album_by_album on view__library_tracks_with_album (albumName);
create  index if not exists view__library_tracks_with_album_by_name on view__library_tracks_with_album (name);
create virtual view if not exists view__library_tracks_with_album_and_artist_id as 
      select *
        from view__library_tracks_with_album as _tracks
        left outer join library_tracks_artists as _tracks_artists on _tracks.id = _tracks_artists.trackId
    ;
create  index if not exists view__library_tracks_with_album_and_artist_id_by_id on view__library_tracks_with_album_and_artist_id (id);
create  index if not exists view__library_tracks_with_album_and_artist_id_by_artistId on view__library_tracks_with_album_and_artist_id (artistId);
create virtual view if not exists view__library_tracks_with_album_and_artist as 
      select
        -- TrackSrc data
        _tracks.id as id,
        _tracks.name as name,
        _tracks.imageUrl as imageUrl,
        _tracks.description as description,
        _tracks.externalLink as externalLink,
        albumName,
        albumId,
        albumTrackCount,
        albumColorsString,
        durationMs,
        trackNumber,
        -- Artist data
        _artists.id as artistId,
        _artists.name as artistName
        from view__library_tracks_with_album_and_artist_id as _tracks
        left outer join library_artists as _artists on _artists.id = _tracks.artistId
    ;
create  index if not exists view__library_tracks_with_album_and_artist_by_id on view__library_tracks_with_album_and_artist (id);
create virtual view if not exists view__tracks_player_context_items as select * from view__library_tracks_with_album_and_artist, player_context_items where player_context_items.trackId = view__library_tracks_with_album_and_artist.id;
create  index if not exists view__tracks_player_context_items_by_contextId_and_sortIndex on view__tracks_player_context_items (contextId, sortIndex);
create  index if not exists view__tracks_player_context_items_by_id on view__tracks_player_context_items (id);
create virtual view if not exists view__tracks_playlists as 
      select *
      from view__library_tracks_with_album_and_artist, library_tracks_playlists
      where library_tracks_playlists.trackId = view__library_tracks_with_album_and_artist.id
    ;
create  index if not exists view__tracks_playlists_by_playlistId on view__tracks_playlists (playlistId);
create virtual view if not exists view__playlists_with_track_counts as 
      SELECT _p.id, _p.name, _p.description, _p.ownerId, _p.ownerName, _p.colorsString, _p.source, _p.imageUrl, count(_tp.trackId) as trackCount
      FROM view__tracks_playlists AS _tp
      LEFT OUTER JOIN library_playlists AS _p ON _p.id = _tp.playlistId 
      GROUP BY _p.id
    ;
create  index if not exists view__playlists_with_track_counts_on_id on view__playlists_with_track_counts (id);
create virtual view if not exists view__artists_with_track_counts as 
      SELECT _a.id, _a.name, _a.colorsString, _a.imageUrl, count(*) as trackCount
      FROM library_artists AS _a
      LEFT OUTER JOIN view__library_tracks_with_album_and_artist AS _ta ON _a.id = _ta.artistId
      GROUP BY _a.id
    ;
create  index if not exists view__artists_with_track_counts_on_id on view__artists_with_track_counts (id);
create virtual view if not exists view__album_artists as 
      select * from library_artists
      inner join library_artists_albums on library_artists_albums.artistId = library_artists.id
    ;
create  index if not exists view__album_artists_by_albumId on view__album_artists (albumId);
